import"https://unpkg.com/boxicons@2.1.4/dist/boxicons.js";window.addEventListener("scroll",()=>{const e=window.scrollY,o=document.getElementById("topButton");e>1e3?(o?.classList.add("show-button"),o?.classList.remove("hide-button"),o?.classList.remove("hidden")):(o?.classList.remove("show-button"),o?.classList.add("hide-button"))});document.querySelectorAll(".pageHref").forEach(function(e){e.addEventListener("click",function(o){o.preventDefault();const t=document.getElementById(this.getAttribute("href").substring(1));if(["start","about"].includes(t?t.id:"")){const n=t?t.getBoundingClientRect().top+window.scrollY:0,s=window.innerHeight/2-(t?t.offsetHeight/2:0),i=n-s;window.scrollTo({top:i,behavior:"smooth"})}else{const n=t?t.getBoundingClientRect().top+window.scrollY:0;window.scrollTo({top:n,behavior:"smooth"})}})});
